import styled from "styled-components";
import { Course } from "../../../data/course";

interface ICourseItem {
  course: Course;
}

const CourseItem = ({ course }: ICourseItem) => {
  return (
    <CourseContainer color={course.bgColor}>
      <Content>
        <Issuer>
          <span>{course.issuer}</span>
        </Issuer>
        <div>
          <CourseName>{course.courseName}</CourseName>
          <CourseDate>{course.date}</CourseDate>
        </div>
      </Content>
      <CenterPattern>
        <Pattern1 color={course.lineColor} />
      </CenterPattern>
      <LeftPattern>
        <Pattern2 color={course.hubColor} />
      </LeftPattern>
    </CourseContainer>
  );
};

const LeftPattern = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  z-index: 0;
`;

const CenterPattern = styled.div`
  position: absolute;
  top: 0;
  left: 15%;
  z-index: 0;
`;

const Content = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 2;

  padding: 1.5rem 2rem;

  display: flex;
  justify-content: space-between;
  flex-direction: column;
`;

const Issuer = styled.div`
  font-weight: 500;
`;

const CourseName = styled.span`
  font-weight: 500;
  font-size: 18px;
  line-height: 111%;
  text-transform: lowercase;

  display: block;
`;

const CourseDate = styled.span`
  font-weight: 300;
  font-size: 13px;
  line-height: 16px;

  padding-top: 1rem;
  display: block;
`;

